mapper.select:

select id, project_id, code, rec_time, data_value, state, remarks
from result_day
where id = #{id,jdbcType=INTEGER}

select id, project_id, code, rec_time, data_value, state, remarks
from result_day
where project_id = #{projectId} and code = #{code}
and rec_time = str_to_date(#{recTime},'%Y-%m-%d %H:%i:%s');

mapper.update:

update result_day
set
rec_time = #{recTime,jdbcType=TIMESTAMP},
sys_time = now(),
data_value = #{dataValue,jdbcType=DOUBLE}
where project_id = #{projectId,jdbcType=VARCHAR} and  code = #{code,jdbcType=VARCHAR} and  rec_time = #{recTime,jdbcType=TIMESTAMP}
