mapper.select:

select id, project_id, code, rec_time, data_value, state, remarks
from result_hour
where id = #{id,jdbcType=INTEGER}

select id, project_id, code, rec_time, data_value, state, remarks
from result_hour
where project_id = #{projectId} and code = #{code}
and rec_time = str_to_date(#{recTime},'%Y-%m-%d %H:%i:%s');
